import UIKit

struct RegistrationConfigurator {

	static func createModule() -> UIViewController {
        var view: RegistrationViewController!

        let viewController = UIStoryboard.init(name: "Registration", bundle: Bundle.main).instantiateInitialViewController()
        if viewController == nil {
            fatalError("Seems there is no initial view controller in Registration.storyboard")
        }

        if viewController is UINavigationController {
            view = (viewController as! UINavigationController).viewControllers.first as? RegistrationViewController
        } else {
            view = viewController as? RegistrationViewController
        }

        let interactor = RegistrationInteractor()
        let router = RegistrationRouter()
        let presenter = RegistrationPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return viewController!
    }
}
